import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';

function ProhlidkyNemovitosti() {
  const { id } = useParams(); // Získání ID nemovitosti z URL
  const [prohlidky, setProhlidky] = useState([]);
  const [nemovitost, setNemovitost] = useState(null);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingProhlidka, setEditingProhlidka] = useState(null);
  const { register, handleSubmit, reset, setValue, formState: { errors, isValid } } = useForm({
    mode: 'onChange', // Validace při každé změně
  });

  // Načtení nemovitosti a prohlídek
  useEffect(() => {
    // Načtení detailů nemovitosti
    fetch(`http://localhost:3001/api/nemovitosti`)
      .then(response => response.json())
      .then(data => {
        const foundNemovitost = data.find(n => n._id === id);
        setNemovitost(foundNemovitost);
      })
      .catch(error => console.error('Chyba při načítání nemovitosti:', error));

    // Načtení prohlídek
    fetch(`http://localhost:3001/api/prohlidky/nemovitost/${id}`)
      .then(response => response.json())
      .then(data => setProhlidky(data))
      .catch(error => console.error('Chyba při načítání prohlídek:', error));
  }, [id]);

  // Odeslání formuláře (vytvoření nebo editace prohlídky)
  const onSubmit = async (data) => {
    console.log('Formulář odeslán s daty:', data); // Ladění
    try {
      const url = editingProhlidka
        ? `http://localhost:3001/api/prohlidky/${editingProhlidka._id}`
        : `http://localhost:3001/api/prohlidky`;
      const method = editingProhlidka ? 'PUT' : 'POST';

      // Převod data na UTC pro backend
      const datum = new Date(data.datum).toISOString();

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          nemovitostId: id,
          datum,
          jmenoZajemce: data.jmenoZajemce,
          telefon: data.telefon || '',
          email: data.email || ''
        }),
      });

      if (response.ok) {
        const updatedProhlidka = await response.json();
        if (editingProhlidka) {
          setProhlidky(prohlidky.map(p => (p._id === editingProhlidka._id ? updatedProhlidka : p)));
        } else {
          setProhlidky([...prohlidky, updatedProhlidka]);
        }
        setIsFormOpen(false);
        setEditingProhlidka(null);
        reset();
      } else {
        console.error('Chyba při ukládání prohlídky:', response.statusText);
      }
    } catch (error) {
      console.error('Chyba při odesílání formuláře:', error);
    }
  };

  // Otevření formuláře pro editaci
  const handleEdit = (prohlidka) => {
    setEditingProhlidka(prohlidka);
    setIsFormOpen(true);
    const localDate = new Date(prohlidka.datum);
    const year = localDate.getFullYear();
    const month = String(localDate.getMonth() + 1).padStart(2, '0');
    const day = String(localDate.getDate()).padStart(2, '0');
    const hours = String(localDate.getHours()).padStart(2, '0');
    const minutes = String(localDate.getMinutes()).padStart(2, '0');
    const localDateTime = `${year}-${month}-${day}T${hours}:${minutes}`;
    setValue('datum', localDateTime);
    setValue('jmenoZajemce', prohlidka.jmenoZajemce);
    setValue('telefon', prohlidka.telefon || '');
    setValue('email', prohlidka.email || prohlidka.kontakt || '');
  };

  // Smazání prohlídky
  const handleDelete = async (prohlidkaId) => {
    try {
      const response = await fetch(`http://localhost:3001/api/prohlidky/${prohlidkaId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setProhlidky(prohlidky.filter(p => p._id !== prohlidkaId));
      } else {
        console.error('Chyba při mazání prohlídky:', response.statusText);
      }
    } catch (error) {
      console.error('Chyba při mazání:', error);
    }
  };

  // Otevření formuláře pro novou prohlídku
  const handleCreate = () => {
    setEditingProhlidka(null);
    setIsFormOpen(true);
    reset();
  };

  return (
    <div className="container">
      <header style={{ display: 'flex', alignItems: 'center', marginBottom: '2rem' }}>
        <Link to="/dashboard" style={{ display: 'flex', alignItems: 'center', textDecoration: 'none', color: 'inherit' }}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
            stroke="#2563eb"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
            style={{ marginRight: '0.5rem' }}
          >
            <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
            <polyline points="9 22 9 12 15 12 15 22"></polyline>
          </svg>
          <h1 style={{ fontSize: '1.5rem', margin: 0 }}>ProperTea</h1>
        </Link>
      </header>

      <h2>Prohlídky nemovitosti {nemovitost ? nemovitost.adresa : 'Načítání...'}</h2>
      <button
        onClick={handleCreate}
        style={{
          backgroundColor: '#2563eb',
          color: 'white',
          padding: '0.5rem 1rem',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
          marginBottom: '1rem',
        }}
      >
        Přidat novou prohlídku
      </button>

      {/* Seznam prohlídek */}
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {prohlidky.length === 0 ? (
          <li>Žádné prohlídky pro tuto nemovitost</li>
        ) : (
          prohlidky.map(prohlidka => (
            <li
              key={prohlidka._id}
              style={{
                padding: '0.5rem',
                border: '1px solid #ccc',
                marginBottom: '0.5rem',
                borderRadius: '4px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
              }}
            >
              <span>
                {new Date(prohlidka.datum).toLocaleString('cs-CZ')} - {prohlidka.jmenoZajemce} (
                {prohlidka.email || prohlidka.kontakt || 'bez emailu'}
                {prohlidka.telefon ? `, ${prohlidka.telefon}` : ''})
              </span>
              <div>
                <button
                  onClick={() => handleEdit(prohlidka)}
                  style={{
                    backgroundColor: '#10b981',
                    color: 'white',
                    padding: '0.3rem 0.8rem',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginRight: '0.5rem',
                  }}
                >
                  Editovat
                </button>
                <button
                  onClick={() => handleDelete(prohlidka._id)}
                  style={{
                    backgroundColor: '#dc2626',
                    color: 'white',
                    padding: '0.3rem 0.8rem',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                  }}
                >
                  Smazat
                </button>
              </div>
            </li>
          ))
        )}
      </ul>

      {/* Formulář pro vytvoření/editaci prohlídky */}
      {isFormOpen && (
        <div
          style={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: 'white',
            padding: '2rem',
            border: '1px solid #ccc',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
            zIndex: 1000,
          }}
        >
          <h2>{editingProhlidka ? 'Editovat prohlídku' : 'Přidat prohlídku'}</h2>
          <form onSubmit={handleSubmit(onSubmit)} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            <label>
              Datum a čas: <span style={{ color: 'red' }}>{errors.datum && '(Povinné)'}</span>
              <input
                type="datetime-local"
                {...register('datum', { required: true })}
                style={{ padding: '0.5rem', border: errors.datum ? '1px solid red' : '1px solid #ccc' }}
              />
            </label>
            <label>
              Jméno zájemce: <span style={{ color: 'red' }}>{errors.jmenoZajemce && '(Povinné)'}</span>
              <input
                {...register('jmenoZajemce', { required: true })}
                style={{ padding: '0.5rem', border: errors.jmenoZajemce ? '1px solid red' : '1px solid #ccc' }}
              />
            </label>
            <label>
              Telefon:
              <input
                {...register('telefon')}
                style={{ padding: '0.5rem', border: '1px solid #ccc' }}
              />
            </label>
            <label>
              Email:
              <input
                type="email"
                {...register('email')}
                style={{ padding: '0.5rem', border: '1px solid #ccc' }}
              />
            </label>
            <div style={{ display: 'flex', gap: '1rem' }}>
              <button
                type="submit"
                disabled={!isValid}
                style={{
                  backgroundColor: isValid ? '#2563eb' : '#cccccc',
                  color: 'white',
                  padding: '0.5rem 1rem',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: isValid ? 'pointer' : 'not-allowed',
                }}
              >
                {editingProhlidka ? 'Uložit změny' : 'Vytvořit'}
              </button>
              <button
                type="button"
                onClick={() => {
                  setIsFormOpen(false);
                  setEditingProhlidka(null);
                  reset();
                }}
                style={{
                  backgroundColor: '#6b7280',
                  color: 'white',
                  padding: '0.5rem 1rem',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                }}
              >
                Zrušit
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default ProhlidkyNemovitosti;
