import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Showings() {
  const [showings, setShowings] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/api/showings")
      .then((response) => response.json())
      .then((data) => setShowings(data))
      .catch((error) => console.error("Error fetching showings:", error));
  }, []);

  return (
    <div className="container">
      <header
        style={{ display: "flex", alignItems: "center", marginBottom: "2rem" }}
      >
        <Link
          to="/dashboard"
          style={{
            display: "flex",
            alignItems: "center",
            textDecoration: "none",
            color: "inherit",
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
            stroke="#2563eb"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
            style={{ marginRight: "0.5rem" }}
          >
            <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
            <polyline points="9 22 9 12 15 12 15 22"></polyline>
          </svg>
          <h1 style={{ fontSize: "1.5rem", margin: 0 }}>ProperTea</h1>
        </Link>
      </header>
      <nav className="sub-nav" style={{ marginBottom: "2rem" }}>
        <Link
          to="/properties"
          style={{
            marginRight: "1rem",
            textDecoration: "none",
            fontWeight: "bold",
          }}
        >
          Properties
        </Link>
        <Link
          to="/showings"
          style={{
            textDecoration: "none",
            fontWeight: "bold",
          }}
        >
          Showings
        </Link>
      </nav>

      <h2>All Showings</h2>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {showings.length === 0 ? (
          <li>No showings available</li>
        ) : (
          showings.map((showing) => (
            <li
              key={showing._id}
              style={{
                padding: "0.5rem",
                border: "1px solid #ccc",
                marginBottom: "0.5rem",
                borderRadius: "4px",
              }}
            >
              <Link
                to={`/properties/${showing.propertyId._id}/showings`}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                {new Date(showing.date).toLocaleString("en-US")} - {showing.clientName} (
                {showing.email || "no email"}
                {showing.phone ? `, ${showing.phone}` : ""}) - Property: {showing.propertyId.address}
              </Link>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default Showings;
